import { useState } from "react";

const Statistics = (props) => {

  if (props.arr == 0){
    return ( <div>NO feedback given</div> 
  )}

  const sum = props.arr.reduce((acm , sum) => sum + acm , 0 )
  const total = props.arr.length

  return (

    <div>

      <span>all {props}</span> 
      <span>avg {props.value}</span> 
      <span>positive {props.value }</span>
    </div>
  )
}

function App() {
  const [good , setGood] = useState(0)  
  const [neutral , setNeutral] = useState(0)  
  const [bad , setBad] = useState(0)  


  const [allClicks, setAll] =  useState([])

  const handleGood = () =>{
    setAll([...allClicks, 1])
  }
  const handleNeutral = () =>{
    setAll([...allClicks, 0])
  }
  const handleBad = () =>{
    setAll([...allClicks, -1])
  }


  return (
      <div>
        <h1> Give feedback </h1>
        <button onClick={handleGood}>good</button>
        <button onClick={handleNeutral}>neutral</button>
        <button onClick={handleBad}>bad</button>
        <h1> Statistics </h1>
        <Statistics arr= {allClicks} />


      </div>
    )
}


export default App;
